  },
  data: function data() {
    return {
      filterPriceFrom: 0,
      filterPriceTo: 0,
      filterCategoryId: 0,
      filterColorId: 0,
      page: 1,
      productsPerPage: 3
    };
  },
  computed: {
    filteredProducts: function filteredProducts() {
      var _this = this;
      var filteredProducts = products;
      if (this.filterPriceFrom > 0) {
        filteredProducts = filteredProducts.filter(function (product) {
          return product.price > _this.filterPriceFrom;
        });
      }
      if (this.filterPriceTo > 0) {
        filteredProducts = filteredProducts.filter(function (product) {
          return product.price < _this.filterPriceTo;
        });
      }
      if (this.filterCategoryId) {
        filteredProducts = filteredProducts.filter(function (product) {
          return product.categoryId === _this.filterCategoryId;
        });
      }

      // if(this.filterColorId) {
      //   filteredProducts = filteredProducts.filter(product => product.colorId === this.filterColorId);
      // }

      return filteredProducts;
    },
    products: function products() {
      var offset = (this.page - 1) * this.productsPerPage;
      return this.filteredProducts.slice(offset, offset + this.productsPerPage);
    },
    countProducts: function countProducts() {
      return this.filteredProducts.length;
    }
  }
